INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (3, 'Kamp Tiha Šilo', 'Kamp Tiha Šilo se nahaja na vzhodni strani otoka Krk, na samotnem polotoku v kraju Šilo do katerega lahko pridete po cesti preko kraja Malinska ali pa po novi lokalni cesti Omišalj - Čižići - Klimno - Šilo.', '051897852', 'Konjska bb, 51515 Šilo', 'Šilo', '45.145827', '14.662310', 1);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (7, 'Kamp Lucija', 'Kamp Lucija je urejen na zelenem polotoku Seča. Kamp ima urejenih okoli 550 parcel in lahko sprejme do 1.000 gostov. Od centra Portoroža, največjega slovenskega obmorskega letovišča, je kamp oddaljen slab kilometer.', '041631789', 'Seča 204, 6320 Portorož', 'Portorož', '45.502124', '13.600714', 4);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (8, 'Kamp Fiesa', 'Kamp Fiesa leži v zelenem, mirnem zalivu Fiesa, ki je od Portoroža oddaljen le 3 km, in je znan tudi po dveh sladkovodnih jezercih. Kamp je od morja oddaljen le 100 m, njegova kapaciteta pa je 112 prikolic, 35 šotorišč ter manjši prostor za avtodome.', '031390561', 'Fiesa 57b, 6330 Piran', 'Piran', '45.524112', '13.580028', 4);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (9, 'Kamp Bled', 'Kamp Bled obratuje že vse od leta 1950. Leži na zahodni strani Blejskega jezera, od katerega je oddaljen cca 150 metrov. Prednosti kampa sta idilična lega v lepem naravnem okolju v neposredni bližini jezera in kratka oddaljenost od centra Bleda (2km). Gre za miren kamp, kjer uživajo ljubitelji lepe narave, pohodništva in kolesarjenja.', '051456911', 'Kidričeva 10 C, 4260 Bled', 'Bled', '46.361734', '14.080963', 6);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (10, 'Kamp Nadiža', 'Kamp Nadiža leži v skrivnostnem Breginjskem kotu leži na dnu doline v vasici Podbela, ki je od Kobarida oddaljena 11 kilometrov. V akciji Naj kamp Adria 2018 so kampisti izbrali kamp Nadiža kot tretji najboljši slovenski kamp v kategorij velikih kampov v Sloveniji.', '031888999', 'Stresova ulica 18, 5222 Kobarid', 'Kobarid', '46.242990', '13.457622', 7);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (11, 'Kamp Green Park', 'Kamp Green Park leži v kraju Žitomislići, 17 km južneje od Mostarja. Lokacija kampa je tik ob reki Neretvi in v bližini magistralne ceste, ki povezuje Mostar in Neum.', '041234234', 'Žitomislići bb, 88268 Mostar', 'Mostar', '43.200937', '17.796588', 8);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (12, 'Kamp Budva', 'Kamp Budva se nahaja v središču Budve in je od morja oz. Slovenske plaže oddaljen 500 m. Kamp ima cca 70 parcel, urejene sanitarije in dobro senco. Trgovina in restavracije so v neposredni bližini kampa.', '031963123', 'V ulica broj 1, Velji Vinograd', 'Budva', '42.286911', '18.845687', 9);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (13, 'Kamp Enigma', 'Kamp Enigma je nov kamp v južno Srbiji, ki je namenjen kampistom na poti proti Makedoniji in Grčiji. Leži 2,5 km od kraja Vranje in avtoceste Beograd - Skopje. Od Niša je kamp oddaljen 115 km, od srbsko - makedonske meje pa 45 km.', '051211777', 'Kupinince bb, 17500 Vranje', 'Vranje', '42.530977', '21.878494', 5);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (14, 'Kamp Pineta', 'Kamp Pineta Fažana se nahaja 2 km severno od Fažane, ki je od Pule oddaljena 5 km. Kamp ima urejene parcel v borovem gozdu, na travniku na levi strani kampa pa je neparceliran del, ki pa ima manj sence.  V kampu omogočajo tudi najem agencijskih mobilnih hišic Adriatic Kampovi', '041789987', 'Perojska cesta 41, 52212 Fažana', 'Fažana', '44.938806', '13.795504', 3);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (15, 'Camping Šobec', 'Kamp Šobec je že vrsto let uvrščen med najboljše kampe v Sloveniji. Razteza se v lepem borovem gozdu v bližini Lesc, med majhnim jezerom in reko Savo, z enkratnim pogledom na Julijske Alpe in Karavanke. Nahaja se samo 3 km od avtoceste Jesenice – Ljubljana, ob cesti, ki vodi od Lesc proti Bledu, ki je oddaljen le 3km.', '031444555', 'Šobčeva cesta 25, 4248 Lesce', 'Lesce', '46.353943', '14.148780', 6);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (2, 'Kamp Straško', 'Kamp Straško leži v kraju Novalja na otoku Pag. Kamp je urejen ob 2 km dolgi prodnati plaži. Je eden najbolj znanih hrvaških kampov in največji kamp na otoku Pagu. Celoten kamp Straško ima odlično senco hrastovih dreves, ki segajo vse do obale.', '013211232', 'Zeleni put 7, 53291 Novalja', 'Straško', '44.545390', '14.880870', 2);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (1, 'Kamp Njivice', 'Kamp Njivice je uvrščen med najboljše hrvaške kampe se nahaja ob morju obdan je s hrastovim gozdom, zaradi česar je v njem idealna senca na novo urejene parcele tudi do 120m2 elektrika in voda na vsaki parceli edinstvene mobilne hišice ob samem morju Cabana bar&more prelep lounge bar na plaži', '083211232', 'Morska ulica 113, 1000 Krk', 'Njivice', '45.180348', '14.545060', 1);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija)VALUES (4, 'Kamp Stella', 'Kamp Stella Maris Umag je bil v letu 2018 popolnoma prenovljen in je dobil novo recepcijo, novo urejene kamping parcele, bazen, restavracijo, ponudbo pa bodo dopolnili zmožnostjo najema modernih mobilnih hišic.', '041987789', 'Stella Maris 8a, 52470 Umag', 'Umag', '45.454242', '13.521910', 3);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (5, 'Kamp Zaton Holiday', 'Kamp Zaton je eden najboljših hrvaških kampov in se nahaja 16 km severno od Zadra oz. 1,5 km zahodno on kraja Nin. Gre za moderno urejen kamp z 1,5 km dolgo mivkasto in peščeno plažo. V kampu Zaton omogočajo kampiranje na urejenih parcelah, najem mobilnih hišic in glamping šotorov. V sklopu naselja omogočajo tudi najem številnih apartmajev.', '045123789', 'Dražnikova 76t, 23232 Nin', 'Nin', '44.222144', '15.171727', 2);
INSERT INTO avtokampi (avtokamp_id, naziv, opis, telefon, naslov, naziv_lokacije, koordinata_x, koordinata_y, regija) VALUES (6, 'Kamp Slanica', 'Kamp Slanica Murter se nahaja 2 km zahodno od kraja Murter, direktno ob plaži, ki pa je zelo raznolika. Del plaže je prodnat z mivko v vodi, del pa tudi skalnat. Iz kampa je prekrasen pogled na Kornate. V kampu Slanica omogočajo kampiranje na urejenih parcelah, najem mobilnih hišic in apartmajev.', '045897321', 'Podvrške 8, 22243 Murter', 'Murter', '43.817163', '15.576704', 2);
